package main

import (
	"testing"
)

func TestGetLongestPrefix(t *testing.T) {
	ina := "abcdeefg"
	inb := "abcdegfe"

	want := "abcde"
	msg := getLongestPrefix(ina, inb)
	if want != msg {
		t.Errorf(`getLongestPrefix() = %q, want %q`, msg, want)
	}
}

func TestGetLongestPrefixMassive(t *testing.T) {
	ina := ""
	inb := ""

	want := ""
	msg := getLongestPrefix(ina, inb)
	if want != msg {
		t.Errorf(`getLongestPrefix() = %q, want %q`, msg, want)
	}
}
